import React, { useEffect, useState } from 'react';
import { useLocation, useNavigate } from 'react-router-dom';
import { ToastContainer } from 'react-toastify';
import { handleError, handleSuccess } from '../../utils';
import './VerifyOtp.css';

function VerifyOtp() {
    const navigate = useNavigate();
    const location = useLocation();

    // 1. Recover email from location or localStorage
    const [email, setEmail] = useState(location.state?.email || localStorage.getItem('pendingEmail') || '');
    const [otp, setOtp] = useState('');

    // 2. If email from location, save it to localStorage
    useEffect(() => {
        if (location.state?.email) {
            localStorage.setItem('pendingEmail', location.state.email);
        }
    }, [location.state]);

    const handleVerify = async (e) => {
        e.preventDefault();

        if (!email || !otp) {
            return handleError('Email and OTP are required.');
        }

        try {
            const response = await fetch('http://localhost:4000/auth/login', {
                method: 'POST',
                headers: {
                    'Content-Type': 'application/json',
                },
                body: JSON.stringify({ email, otp }),
            });

            const data = await response.json();

            if (data.success) {
                handleSuccess('OTP verified successfully!');
                localStorage.setItem('token', data.jwtToken);
                localStorage.setItem('loggedInUser', data.name);
                localStorage.setItem('loggedInEmail', email);
                localStorage.removeItem('pendingEmail'); // ✅ clean up

                setTimeout(() => navigate('/dashboard'), 1000);
            } else {
                handleError(data.message || 'Invalid OTP.');
            }
        } catch (err) {
            console.error(err);
            handleError('Something went wrong. Try again.');
        }
    };

    return (
        <div className="verify-container">
            <div className="verify-box">
                <div className="logo">
                    <div className="spinner" />
                    <span className="brand">HD</span>
                </div>
                <h2>Verify OTP</h2>
                <p>Please enter the OTP sent to <strong>{email}</strong></p>
                <form onSubmit={handleVerify} className="verify-form">
                    <input
                        type="text"
                        placeholder="Enter OTP"
                        value={otp}
                        onChange={(e) => setOtp(e.target.value)}
                        className="otp-input"
                    />
                    <button type="submit" className="verify-btn">Verify</button>
                </form>
                <p className="resend-text">Didn’t receive the OTP? <span className="link">Resend OTP</span></p>
            </div>
            <ToastContainer position="top-center" autoClose={3000} />
        </div>
    );
}

export default VerifyOtp;
