import React, { useState } from 'react';
import { Link, useNavigate } from 'react-router-dom';
import { ToastContainer } from 'react-toastify';
import { handleError, handleSuccess } from '../utils';
import '../styles/Signup.css'; // Assuming CSS is written here

function Signup() {
    const [signupInfo, setSignupInfo] = useState({
        name: '',
        dob: '',
        email: '',
    });

    const navigate = useNavigate();

    const handleChange = (e) => {
        const { name, value } = e.target;
        setSignupInfo(prev => ({ ...prev, [name]: value }));
    };

    const handleSignup = async (e) => {
        e.preventDefault();
        const { name, dob, email } = signupInfo;

        if (!name || !dob || !email) {
            return handleError('All fields are required');
        }

        try {
            const url = `http://localhost:4000/auth/signup`;
            const response = await fetch(url, {
                method: "POST",
                headers: { 'Content-Type': 'application/json' },
                body: JSON.stringify(signupInfo)
            });

            const result = await response.json();
            const { success, message, error } = result;

            if (success) {
                handleSuccess(message || 'OTP sent');
                setTimeout(() => navigate('/verify-otp'), 1000);
            } else if (error?.details?.[0]?.message) {
                handleError(error.details[0].message);
            } else {
                handleError(message || 'Signup failed');
            }

        } catch (err) {
            handleError('Network error or server is unreachable');
            console.error(err);
        }
    };

    return (
        <div className="signup-container">
            <div className="logo">üåê HD</div>
            <h2 className="signup-title">Sign up</h2>
            <p className="signup-subtitle">Sign up to enjoy the feature of HD</p>

            <form className="signup-form" onSubmit={handleSignup}>
                <input
                    type="text"
                    name="name"
                    placeholder="Your Name"
                    value={signupInfo.name}
                    onChange={handleChange}
                    className="signup-input"
                />
                <div className="dob-container">
                    <span className="calendar-icon">üìÖ</span>
                    <input
                        type="text"
                        name="dob"
                        placeholder="Date of Birth"
                        value={signupInfo.dob}
                        onChange={handleChange}
                        className="signup-input dob-input"
                    />
                </div>
                <input
                    type="email"
                    name="email"
                    placeholder="Email"
                    value={signupInfo.email}
                    onChange={handleChange}
                    className="signup-input"
                />

                <button type="submit" className="otp-button">Get OTP</button>

                <p className="login-link">
                    Already have an account?? <Link to="/login">Sign in</Link>
                </p>
            </form>

            <ToastContainer position="top-center" autoClose={3000} />
        </div>
    );
}

export default Signup;
