import React, { useEffect, useState } from 'react';
import { useNavigate } from 'react-router-dom';
import './Dashboard.css';

function Dashboard() {
    const navigate = useNavigate();
    const [user, setUser] = useState({ name: '', email: '' });
    const [notes, setNotes] = useState([]);
    const [newNote, setNewNote] = useState('');

    useEffect(() => {
        const token = localStorage.getItem('token');
        const name = localStorage.getItem('loggedInUser');
        const email = localStorage.getItem('loggedInEmail');

        if (!token) {
            navigate('/login');
        }

        setUser({ name, email });
        fetchNotes();
    }, []);

    const fetchNotes = () => {
        // This would be replaced with API call to fetch notes
        setNotes(['Note 1', 'Note 2']);
    };

    const handleLogout = () => {
        localStorage.clear();
        navigate('/login');
    };

    const handleAddNote = () => {
        if (!newNote.trim()) return;
        setNotes([...notes, newNote]);
        setNewNote('');
    };

    const handleDeleteNote = (index) => {
        const filtered = notes.filter((_, i) => i !== index);
        setNotes(filtered);
    };

    return (
        <div className="dashboard-container">
            {/* Header */}
            <div className="dashboard-header">
                <div className="logo-area">
                    <div className="spinner" />
                    <h2>Dashboard</h2>
                </div>
                <button className="signout-btn" onClick={handleLogout}>Sign Out</button>
            </div>

            {/* User Card */}
            <div className="user-card">
                <h3>Welcome, {user.name || 'User'} !</h3>
                <p>Email: {user.email ? user.email : 'xxxxxx@xxxx.com'}</p>
            </div>

            {/* Create Note */}
            <input
                type="text"
                placeholder="Write your note..."
                value={newNote}
                onChange={(e) => setNewNote(e.target.value)}
                className="note-input"
            />
            <button className="create-note-btn" onClick={handleAddNote}>Create Note</button>

            {/* Notes List */}
            <div className="notes-list">
                <h4>Notes</h4>
                {notes.map((note, index) => (
                    <div key={index} className="note-item">
                        <span>{note}</span>
                        <button onClick={() => handleDeleteNote(index)} className="delete-btn">üóëÔ∏è</button>
                    </div>
                ))}
            </div>
        </div>
    );
}

export default Dashboard;
