import React, { useState } from 'react';
import { Link, useNavigate } from 'react-router-dom';
import { ToastContainer } from 'react-toastify';
import { handleError, handleSuccess } from '../utils';
import '../styles/Signup.css';
import signupBg from '../assets/signup-bg.png';

function Signup() {
    const [signupInfo, setSignupInfo] = useState({
        name: '',
        dob: '',
        email: '',
    });

    const navigate = useNavigate();

    const handleChange = (e) => {
        const { name, value } = e.target;
        setSignupInfo(prev => ({ ...prev, [name]: value }));
    };

    const handleSignup = async (e) => {
        e.preventDefault();
        const { name, dob, email } = signupInfo;

        if (!name || !dob || !email) {
            return handleError('All fields are required');
        }

        try {
            const response = await fetch('http://localhost:4000/auth/signup', {
                method: 'POST',
                headers: {
                    'Content-Type': 'application/json',
                },
                body: JSON.stringify(signupInfo),
            });

            const result = await response.json();

            if (result.success) {
                handleSuccess(result.message || 'OTP sent');
                setTimeout(() => {
                    navigate('/verify-otp', { state: { email } });  // ‚úÖ passing email to OTP page
                }, 1000);
            } else {
                handleError(result?.error?.details?.[0]?.message || result.message || 'Signup failed');
            }
        } catch (err) {
            console.error(err);
            handleError('Network error or server is unreachable');
        }
    };

    return (
        <div className="signup-wrapper">
            <div className="signup-form-section">
                <div className="signup-logo">üåê HD</div>
                <h2 className="signup-title">Sign up</h2>
                <p className="signup-subtitle">Sign up to enjoy the feature of HD</p>

                <form onSubmit={handleSignup} className="signup-form">
                    <input
                        type="text"
                        name="name"
                        placeholder="Your Name"
                        value={signupInfo.name}
                        onChange={handleChange}
                        className="signup-input"
                    />
                    <div className="signup-dob-container">
                        <span className="dob-icon">üìÖ</span>
                        <input
                            type="text"
                            name="dob"
                            placeholder="Date of Birth"
                            value={signupInfo.dob}
                            onChange={handleChange}
                            className="signup-input dob"
                        />
                    </div>
                    <input
                        type="email"
                        name="email"
                        placeholder="Email"
                        value={signupInfo.email}
                        onChange={handleChange}
                        className="signup-input"
                    />
                    <button type="submit" className="signup-btn">Get OTP</button>
                    <p className="signup-switch-text">
                        Already have an account? <Link to="/login">Sign in</Link>
                    </p>
                </form>
            </div>

            <div className="signup-image-section">
                <img src={signupBg} alt="Signup" className="signup-img" />
            </div>

            <ToastContainer position="top-center" autoClose={3000} />
        </div>
    );
}

export default Signup;
