import React, { useEffect, useState } from 'react';
import { useNavigate } from 'react-router-dom';

function Dashboard() {
    const navigate = useNavigate();
    const [user, setUser] = useState({ name: '', email: '' });
    const [notes, setNotes] = useState([]);
    const [newNote, setNewNote] = useState('');

    // Simulate fetching user data from localStorage
    useEffect(() => {
        const token = localStorage.getItem('token');
        const name = localStorage.getItem('loggedInUser');
        const email = localStorage.getItem('loggedInEmail');

        if (!token) {
            navigate('/login');
        }

        setUser({ name, email });
        fetchNotes(); // simulate initial notes
    }, []);

    const fetchNotes = () => {
        // Simulated notes
        setNotes(['Note 1', 'Note 2']);
    };

    const handleLogout = () => {
        localStorage.clear();
        navigate('/login');
    };

    const handleAddNote = () => {
        if (!newNote.trim()) return;
        setNotes(prev => [...prev, newNote.trim()]);
        setNewNote('');
    };

    const handleDeleteNote = (index) => {
        const updated = notes.filter((_, i) => i !== index);
        setNotes(updated);
    };

    return (
        <div className="min-h-screen p-6 bg-white flex flex-col items-center gap-6">
            {/* Header */}
            <div className="w-full flex justify-between items-center">
                <h2 className="text-lg font-semibold flex items-center gap-2">
                    <span className="animate-spin w-5 h-5 rounded-full border-t-2 border-purple-600 border-solid" />
                    Dashboard
                </h2>
                <button onClick={handleLogout} className="text-sm text-blue-600 font-medium">Sign Out</button>
            </div>

            {/* Welcome Box */}
            <div className="w-full max-w-sm border-2 border-pink-500 rounded-md p-4">
                <h1 className="font-bold text-lg mb-1">Welcome, {user.name} !</h1>
                <p className="text-sm text-gray-600">Email: {user.email ? user.email : 'xxxxxx@xxxx.com'}</p>
            </div>

            {/* Create Note */}
            <div className="w-full max-w-sm flex flex-col items-center gap-4">
                <input
                    type="text"
                    value={newNote}
                    onChange={e => setNewNote(e.target.value)}
                    placeholder="Enter note..."
                    className="w-full p-3 border border-gray-300 rounded-lg focus:outline-none"
                />
                <button
                    onClick={handleAddNote}
                    className="bg-indigo-600 text-white px-4 py-2 w-full rounded-lg font-medium"
                >
                    Create Note
                </button>
            </div>

            {/* Notes List */}
            <div className="w-full max-w-sm mt-4">
                <h3 className="font-semibold mb-2">Notes</h3>
                <div className="flex flex-col gap-3">
                    {notes.map((note, index) => (
                        <div key={index} className="flex justify-between items-center bg-gray-100 p-3 rounded-lg shadow">
                            <span>{note}</span>
                            <button onClick={() => handleDeleteNote(index)} className="text-red-600 hover:text-red-800">
                                🗑️
                            </button>
                        </div>
                    ))}
                </div>
            </div>
        </div>
    );
}

export default Dashboard;
