import React, { useEffect, useState } from 'react';
import { useNavigate } from 'react-router-dom';
import './Dashboard.css';

function Dashboard() {
    const navigate = useNavigate();
    const [name, setName] = useState('');
    const [email, setEmail] = useState('');
    const [notes, setNotes] = useState(['Note 1', 'Note 2']);

    useEffect(() => {
        const token = localStorage.getItem('token');
        const storedName = localStorage.getItem('loggedInUser');
        const storedEmail = localStorage.getItem('loggedInEmail');

        if (!token) {
            navigate('/login');
        } else {
            setName(storedName || 'User');
            setEmail(storedEmail || 'xxxxxx@xxxx.com');
        }
    }, [navigate]);

    const handleSignOut = () => {
        localStorage.clear();
        navigate('/login');
    };

    const handleCreateNote = () => {
        const newNote = `Note ${notes.length + 1}`;
        setNotes([...notes, newNote]);
    };

    const handleDeleteNote = (index) => {
        const updatedNotes = notes.filter((_, i) => i !== index);
        setNotes(updatedNotes);
    };

    return (
        <div className="dashboard-container">
            <header className="dashboard-header">
                <div className="spinner" />
                <h2>Dashboard</h2>
                <button className="signout-btn" onClick={handleSignOut}>Sign Out</button>
            </header>

            <div className="user-box">
                <h3>Welcome, {name} !</h3>
                <p>Email: {email}</p>
            </div>

            <button className="create-note-btn" onClick={handleCreateNote}>Create Note</button>

            <div className="notes-section">
                <h4>Notes</h4>
                {notes.map((note, index) => (
                    <div key={index} className="note-card">
                        <span>{note}</span>
                        <button onClick={() => handleDeleteNote(index)}>üóëÔ∏è</button>
                    </div>
                ))}
            </div>
        </div>
    );
}

export default Dashboard;
