import React, { useState } from 'react';
import { Link, useNavigate } from 'react-router-dom';
import { ToastContainer } from 'react-toastify';
import { handleError, handleSuccess } from '../utils';
import '../styles/Login.css'; // Assuming CSS file

function Login() {
    const [loginInfo, setLoginInfo] = useState({
        email: '',
        otp: ''
    });
    const [showOtp, setShowOtp] = useState(false);
    const [keepLoggedIn, setKeepLoggedIn] = useState(false);

    const navigate = useNavigate();

    const handleChange = (e) => {
        const { name, value } = e.target;
        setLoginInfo(prev => ({ ...prev, [name]: value }));
    };

    const handleLogin = async (e) => {
        e.preventDefault();
        const { email, otp } = loginInfo;

        if (!email || !otp) {
            return handleError('Email and OTP are required');
        }

        try {
            const response = await fetch(`http://localhost:4000/auth/login`, {
                method: "POST",
                headers: { 'Content-Type': 'application/json' },
                body: JSON.stringify(loginInfo)
            });

            const result = await response.json();
            const { success, message, jwtToken, name, error } = result;

            if (success) {
                handleSuccess(message || 'Login successful');
                localStorage.setItem('token', jwtToken);
                localStorage.setItem('loggedInUser', name);
                if (keepLoggedIn) localStorage.setItem('keepLoggedIn', true);
                setTimeout(() => navigate('/home'), 1000);
            } else if (error?.details?.[0]?.message) {
                handleError(error.details[0].message);
            } else {
                handleError(message || 'Login failed');
            }
        } catch (err) {
            handleError('Network error or server is unreachable');
            console.error(err);
        }
    };

    return (
        <div className="login-container">
            <div className="logo">üåê HD</div>
            <h2 className="login-title">Sign In</h2>
            <p className="login-subtitle">Please login to continue to your account.</p>

            <form className="login-form" onSubmit={handleLogin}>
                <input
                    type="email"
                    name="email"
                    placeholder="Email"
                    value={loginInfo.email}
                    onChange={handleChange}
                    className="login-input"
                />

                <div className="otp-container">
                    <input
                        type={showOtp ? 'text' : 'password'}
                        name="otp"
                        placeholder="OTP"
                        value={loginInfo.otp}
                        onChange={handleChange}
                        className="login-input"
                    />
                    <span
                        className="toggle-otp"
                        onClick={() => setShowOtp(prev => !prev)}
                        title={showOtp ? "Hide OTP" : "Show OTP"}
                    >
                        {showOtp ? 'üôà' : 'üëÅÔ∏è'}
                    </span>
                </div>

                <div className="resend-otp">
                    <span>Resend OTP</span>
                </div>

                <div className="checkbox-row">
                    <input
                        type="checkbox"
                        id="keepLoggedIn"
                        checked={keepLoggedIn}
                        onChange={(e) => setKeepLoggedIn(e.target.checked)}
                    />
                    <label htmlFor="keepLoggedIn">Keep me logged in</label>
                </div>

                <button type="submit" className="signin-button">Sign In</button>

                <p className="signup-link">
                    Need an account?? <Link to="/signup">Create one</Link>
                </p>
            </form>

            <ToastContainer position="top-center" autoClose={3000} />
        </div>
    );
}

export default Login;
